import { GET_USER, PAGINATE } from "../action/action-type";

let inicialState = {
    allFood:[],
    allFoodBackUp:[],
    currentPage:0
}


function rootReducer(state = inicialState, action){
    switch(action.type){
        case GET_USER: 
            return{
                ...state,
                allFood: action.payload.splice(0, 9),
                allFoodBackUp: action.payload,
            }
            break; 

        case PAGINATE:
            const next_page = state.currentPage + 1;
            const prev_page = state.currentPage - 1;
            const firstIndex = action.payload === "next" ? next_page * 9 : prev_page * 9;

            return{
                ...state,
                allFood: [...state.allFoodBackUp].splice(firstIndex, 9),
                currentPage: action.payload === "next" ? next_page : prev_page
            }


        default:
            return state
            break;
    }

}

export default rootReducer;
