import { GET_USER } from "../action/action-type";

let inicialState = {
    allFood:[],
    allFoodBackUp:[]
}


function rootReducer(state = inicialState, action){
    switch(action.type){
        case GET_USER: 
            return{
                ...state,
                allFood: action.payload,
                allFoodBackUp: action.payload,
            }
            break; 

        default:
            break;
    }

}

export default rootReducer;
