import React from 'react'
import {useParams} from "react-router"
import { cleanDetail, getRecipesById } from '../../redux/action/action'


const Details = () => {

  const {title} = useParams()
  const dispatch = useDispatch() 
  useEffect(() => {
    dispatch(getRecipesById (title));

    return () => {
      dispatch(cleanDetail ()); 
    };
  }, [id]);
  
  const detailsState = useSelector((state) => state.details)

  return (

    <div>
      <div>
        <h1>{detailsState.id}</h1>
      </div>
      <div>
        <h1>{detailsState.title}</h1>
      </div>
      <img className='card-img' src={detailsState.image} alt=""></img>
      <div>
        <h1>{detailsState.summary}</h1>
      </div>
      <div>
        <h1>{detailsState.healthScore}</h1>
      </div>
      <div>
        <h1>{detailsState.analyzedInstructions}</h1>
      </div>
      <div>
        <label>Diets:</label>
        {detailsState.Diets?.map(t => <h1> {t.name}</h1>)} 
      </div>
    {console.log()}
    </div>
  )
}

export default Details
