import React from 'react'
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { cleanDetail, getRecipesById } from '../../redux/action/action'


const Details = () => {

  const {id} = useParams()
  console.log(id)
  const dispatch = useDispatch() 
  useEffect(() => {
    dispatch(getRecipesById(id))
    return () => {
     
    };
  }, [id]);

  const detailState = useSelector((state) => state.allFoodCopy)
  console.log(detailState);
  return (

    <div>
      <div>
        <h3>ID: {detailState[0].id}</h3>
      </div>
      <div>
        <h3>TITLE: {detailState[0].title}</h3>
      </div>
      <img className='card-img' src={detailState[0].image} alt=""></img>
      <div>
        <h3>SUMMARY: {detailState[0].summary}</h3>
      </div>
      <div>
        <h3>HEALTH SCORE: {detailState[0].healthScore}</h3>
      </div>
      <div>
        <h3>ANALYZED INSTRUCTIONS: {detailState[0].analyzedInstructions}</h3>
      </div>
    
    </div>
  );
};

export default Details
