import React from 'react'
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { cleanDetail, getRecipesById } from '../../redux/action/action'


const Details = () => {

  const {id} = useParams()
  console.log(id)
  const dispatch = useDispatch() 
  useEffect(() => {
    console.log('data:',id)
    if(id){
      dispatch(getRecipesById(id))

    }
    return () => {
     
    };
  }, [id]);

  const detailState = useSelector((state) => state.details)
  console.log(detailState);
  if(!detailState || (Array.isArray(detailState) && detailState.length === 0)){
    return (<div>cargando...</div>)
  }
  return (

    <div>
      <div>
        <h3>ID: </h3>
        <h1>{detailState[0].id}</h1>
      </div>
      <div>
        <h3>TITLE: {detailState[0].title}</h3>
      </div>
      <img className='card-img' src={detailState[0].image} alt=""></img>
      <div>
        <h3>SUMMARY: {detailState[0].summary}</h3>
      </div>
      <div>
        <h3>HEALTH SCORE: {detailState[0].healthScore}</h3>
      </div>
      <div>
        {detailState[0].analyzedInstructions?.map((s) => (
          s.steps?.map((r)=>(
            <h3>{step.r}</h3>
          ))
        ))}
      </div>
      <div>
        {detailState[0].diets?.map((t, index) => (
                   <h3 key={index}>{t}</h3>

            ))}
      </div>
    </div>
  );
};

export default Details
