import React from 'react'
import {useParams} from "react-router"
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { cleanDetail, getRecipesById } from '../../redux/action/action'


const Details = () => {

  const {id} = useParams()
  const dispatch = useDispatch() 
  useEffect(() => {
    dispatch(getRecipesById(id))
    return () => {
      dispatch(cleanDetail ()); 
    };
  }, [id]);

  const detailState = useSelector((state) => state.details)

  return (

    <div>
      <div>
        <h3>{detailState.id}</h3>
      </div>
      <div>
        <h1>{detailState.title}</h1>
      </div>
      <img className='card-img' src={detailState.image} alt=""></img>
      <div>
        <h1>{detailState.summary}</h1>
      </div>
      <div>
        <h1>{detailState.healthScore}</h1>
      </div>
      <div>
        <h1>{detailState.analyzedInstructions}</h1>
      </div>
      <div>
        <label>Diets:</label>
        {detailState.Diets?.map(t => <h1> {t.name}</h1>)} 
      </div>
    </div>
  )
}

export default Details
