import axios from "axios"
import { CLEAN_DETAIL, GET_BY_ID, GET_USER, ORDER, PAGINATE } from "./action-type"

export function postFood(state){
    return async function(dispatch){
        try {
            const response = await axios.post("http://localhost:3001/", state)
            alert("Se creo la receta correctamente.")
        } catch (error) {
            alert(error.response.data.error)
        }
    }
}


export function getFood(state){
    return async function(dispatch){
        try {
            const response = await axios.get("http://localhost:3001/")
            dispatch({
                type: GET_USER,
                payload: response.data
            })
        } catch (error) {
            console.log(error)
        }
    }
}


export function pag(dirreccion){
    return async function(dispatch){
        try {
            dispatch({
                type:PAGINATE,
                payload:dirreccion
            })
        } catch (error) {
            console.log(error)
        }
    }
}


export function order(dirreccion){
    return async function(dispatch){
        try {
            dispatch({
                type:ORDER,
                payload:dirreccion
            })
        } catch (error) {
            console.log(error)
        }
    }
}

export function getRecipesById (title){
    
    return async function(dispatch){
        const {data} = await axios.get(`http://localhost:3001/${title}`);
    return dispatch( {
        type : GET_BY_ID,
        payload: data
    })
}
}

export function cleanDetail (payload){
    return {
        type: CLEAN_DETAIL,
        payload
    }
}
