import axios from "axios"

export function postFood(state){
    return async function(dispatch){
        try {
            const response = await axios.post("http://localhost:3001/", state)
            alert("Se creo la receta correctamente.")
        } catch (error) {
            alert("Sucedio un error.")
        }
    }
}


