import React, { useEffect } from 'react';
import Cards from '../../components/cards/cards';
import {useDispatch, useSelector} from "react-redux";
import { getFood, handleNumber, order, orderBySource, pag } from '../../redux/action/action';

import "./home.css";

const Home = () => {
  
  const dispatch = useDispatch();
  const allFood = useSelector((state)=> state.allFood);
  useEffect(() => {
    dispatch(getFood())
    return () => {

    }
  },[])

  const paginate = (event) => {
    dispatch(pag(event.target.name))
  }

  const ordenamiento = (event) => {
    dispatch(order(event.target.name))
  }

  const handleFromApi = (e) => {
  
    dispatch(orderBySource(e.target.value));
    dispatch(handleNumber(1));
  };

  return (
    <div className='home-cont'>
        <h1>FOOD</h1>
        <div>
          <button name="prev" onClick={paginate}>Prev</button>
          <button name="next" onClick={paginate}>Next</button>
        </div>
        <div>
          <button name="az" onClick={ordenamiento}>A - Z</button>
          <button name="za" onClick={ordenamiento}>Z - A</button>  
        </div>
        <div>
            <select onChange={handleFromApi}>
              <option value="ALL">ALL RECIPES</option>
              <option value="API">FROM API</option>
              <option value="BDD">FROM DATABASE</option>
            </select>
          </div>
        <div>
            <Cards info={allFood}/>
        </div>
    </div>
  )
}

export default Home;
