import React, { useEffect } from 'react';
import Cards from '../../components/cards/cards';
import {useDispatch, useSelector} from "react-redux";
import { getFood, order, pag } from '../../redux/action/action';

import "./home.css";

const Home = () => {
  
  const dispatch = useDispatch();
  const allFood = useSelector((state)=> state.allFood)
  useEffect(() => {
    dispatch(getFood())
    return () => {

    }
  },[])

  const paginate = (event) => {
    dispatch(pag(event.target.name))
  }

  const ordenamiento = (event) => {
    dispatch(order(event.target.name))
  }

  return (
    <div className='home-cont'>
        <h1>FOOD</h1>
        <div>
  <select name="pagination" onChange={paginate}>
    <option value="prev">Prev</option>
    <option value="next">Next</option>
  </select>
</div>
<div>
  <select name="sorting" onChange={ordenamiento}>
    <option value="az">A - Z</option>
    <option value="za">Z - A</option>
  </select>
</div>
        <div>
            <Cards info={allFood}/>
        </div>
    </div>
  )
}

export default Home;
