import React, { useState } from 'react';
import { postFood } from '../../redux/action/action';
import {useDispatch} from "react-redux";

import "./create.css"


const Create = () => {

const dispatch = useDispatch();

const [state, setState] = useState({
  nombre:"",
  numero:""
})

const [error, setError] = useState({
  nombre:"Campo requerido",
  numero:"No se permiten letras"
})



const handleChange = (event) =>{
  setState({
    ...state,
    [event.target.name]: event.target.value
  })
  validate({...state,
    [event.target.name]: event.target.value}, event.target.name )
}

const deshabilitar = () => {
  let disabled = true;
  for(let err in error){
    if(error[err]==="")disabled = false;
    else{
      disabled = true;
      break;
    }
  }
return disabled
}


const validate = (state, name) => {
    if(name === "nombre"){
      if(state.nombre !== "")setError({...error, nombre:""})
      else setError({...error, nombre:"Campo requerido"})
    }
    if(name === "numero"){
      if(isNaN(parseInt(state.numero)))setError({...error, numero:"El telefono debe ser numero"})
      else {
        setError({...error, numero:""})
        return;
      }
    }
}


const handleSubmit = (e) =>{
  e.preventDefault()
  dispatch(postFood(state));
}




  return (
  <div className='form-cont'>
    <form onSubmit={handleSubmit}>
    <div>
        <label>Nombre: </label>
        <input name="nombre" onChange={handleChange} type="text" />
        <label className='form-error'>{error.nombre}</label>
        </div>
        <hr className='hr'></hr>
        <div className='form-cont'>
        <label>Numero: </label>
        <input name="numero" onChange={handleChange} type="text" />
        <label className='form-error'>{error.numero}</label>
        <input disabled={deshabilitar()} className='form-button' type="submit" value={"Crear"}/>
    </div>
    </form>
  </div>
  );
};

export default Create
