import axios from "axios"
import { CLEAN_DETAIL, GET_BY_ID, GET_USER, HANDLE_NUMBER, ORDER, ORDER_BY_SOURCE, PAGINATE } from "./action-type"

export function postFood(state){
    return async function(dispatch){
        try {
            const response = await axios.post("http://localhost:3001/", state)
            alert("Se creo la receta correctamente.")
        } catch (error) {
            alert(error.response.data.error)
        }
    }
}


export function getFood(state){
    return async function(dispatch){
        try {
            const response = await axios.get("http://localhost:3001/")
            dispatch({
                type: GET_USER,
                payload: response.data
            })
        } catch (error) {
            console.log(error)
        }
    }
}


export function pag(dirreccion){
    return async function(dispatch){
        try {
            dispatch({
                type:PAGINATE,
                payload:dirreccion
            })
        } catch (error) {
            console.log(error)
        }
    }
}


export function order(dirreccion){
    return async function(dispatch){
        try {
            dispatch({
                type:ORDER,
                payload:dirreccion
            })
        } catch (error) {
            console.log(error)
        }
    }
}

export function getRecipesById(id){
    return async function(dispatch){
        try {
            const response = await axios.get(`http://localhost:3001/${id}`);
            console.log(response);
            dispatch( {
                type :GET_BY_ID,
                payload: response.data,
            });
        } catch (error) {
           console.log(error)
        }
}
}

export function cleanDetail (payload){
    return {
        type: CLEAN_DETAIL,
        payload
    }
}

export function orderBySource (source){
    return{
        type: ORDER_BY_SOURCE,
        payload:source
    }
}

export function handleNumber(num) {
    return {
        type: HANDLE_NUMBER,
        payload: num,
    };
}
