import React from 'react'
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { cleanDetail, getRecipesById } from '../../redux/action/action'


const Details = ({info}) => {

  const {id} = useParams()
  const dispatch = useDispatch() 
  useEffect(() => {
    dispatch(getRecipesById(id))
    return () => {
     
    };
  }, [id]);

  const detailState = useSelector((state) => state.details )
  console.log(detailState);
  return (

    <div>
      <div>
        <h3>ID: {id}</h3>
      </div>
      <div>
        <h3>TITLE: {detailState.title}</h3>
      </div>
      <img className='card-img' src={detailState.image} alt=""></img>
      <div>
        <h3>SUMMARY: {detailState.summary}</h3>
      </div>
      <div>
        <h3>HEALTH SCORE: {detailState.healthScore}</h3>
      </div>
      <div>
        <h3>ANALYZED INSTRUCTIONS: {detailState.analyzedInstructions}</h3>
      </div>
      <div>
        <label>Diets:</label>
        <h3>{detailState.Diets}</h3>
      </div>
    </div>
  );
};

export default Details
