import React, { useState } from 'react'
import "./create.css"


const Create = () => {

const [state, setState] = useState({
  nombre:"",
  numero:""
})

const [error, setError] = useState({
  nombre:"Campo requerido",
  numero:"No se permiten letras"
})



const handleChange = (event) =>{
  setState({
    ...state,
    [event.target.name]: event.target.value
  })
  validate({...state,
    [event.target.name]: event.target.value}, event.target.name )
}

const validate = (state, name) => {
    if(name === "nombre"){
      if(state.nombre !== "")setError({...error, nombre:""})
      else setError({...error, nombre:"Campo requerido"})
    }
    if(name === "numero"){
      if(state.numero !== "")setError({...error, numero:""})
      else setError({...error, numero:"Campo requerido"})
    }
}


const handleSubmit = (e) =>{
  e.preventDefault()
  console.log(state)
}




  return (
    <div className='form-cont'>
      <form onSubmit={handleSubmit}>
        <label>Nombre: </label>
        <input name="nombre" onChange={handleChange} type="text" />
        <label className='form-error'>{error.name}</label>
        <label>Numero: </label>
        <input name="numero" onChange={handleChange} type="text" />
        <label className='form-error'>{error.numero}</label>
        <input className='form-button' type="submit" value={"Crear"}/>
      </form>
    </div>
  )
}

export default Create
