import { GET_USER, ORDER, PAGINATE } from "../action/action-type";

let inicialState = {
    allFood:[],
    allFoodBackUp:[],
    currentPage:0
}


function rootReducer(state = inicialState, action){
    switch(action.type){
        case GET_USER: 
            return{
                ...state,
                allFood: [...action.payload].splice(0, 9),
                allFoodBackUp: action.payload,
            }
            break; 

        case PAGINATE:
            const next_page = state.currentPage + 1;
            const prev_page = state.currentPage - 1;
            const firstIndex = action.payload === "next" ? next_page * 9 : prev_page * 9;
            if(action.payload === "next" && firstIndex >= state.allFoodBackUp.length){ return[...state]}
            else if (action.payload === "prev" && prev_page < 0){ return[...state]}

            return{
                ...state,
                allFood: [...state.allFoodBackUp].splice(firstIndex, 9),
                currentPage: action.payload === "next" ? next_page : prev_page,
                currentPage: 0

            }

        case ORDER:
            if(action.payload === "az"){
                const allFoodOrder = [...state.allFoodBackUp].sort((prev, next) => {
                    if(prev.title > next.title)return 1
                    if(prev.title < next.title)return -1
                    return 0
                })
                return{
                    ...state,
                    allFood:[...allFoodOrder].slice(0, 9),
                    allFoodBackUp: allFoodOrder,
                    currentPage: 0
                }
            }else if(action.payload === "za"){
                const allFoodOrder = [...state.allFoodBackUp].sort((prev, next) => {
                    if(prev.title > next.title)return -1
                    if(prev.title < next.title)return 1
                    return 0
                })
                return{
                    ...state,
                    allFood:[...allFoodOrder].slice(0, 9),
                    allFoodBackUp: allFoodOrder,
                    currentPage: 0
                }
            }



        default:
            return state
            break;
    }

}

export default rootReducer;
