import React from 'react'
import {useParams} from "react-router"
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { getRecipesById } from '../../redux/action/action'


const Details = () => {

  const {id} = useParams()
  const dispatch = useDispatch() 
  useEffect(() => {
    dispatch(getRecipesById(id));
  }, [id]);

  const detailState = useSelector((state) => state.recipe)

  return (

    <div>
      <div>
        <h1>{id}</h1>
      </div>
      <div>
        <h1>{title}</h1>
      </div>
      <img className='card-img' src={image} alt=""></img>
      <div>
        <h1>{summary}</h1>
      </div>
      <div>
        <h1>{healthScore}</h1>
      </div>
      <div>
        <h1>{analyzedInstructions}</h1>
      </div>
      <div>
        <label>Diets:</label>
        {Diets?.map(t => <h1> {t.name}</h1>)} 
      </div>
    </div>
  )
}

export default Details
