import { CLEAN_DETAIL, GET_BY_ID, GET_BY_NAME, GET_TYPE_DIETS, GET_USER, HANDLE_NUMBER, ORDER, ORDER_BY_SOURCE, PAGINATE } from "../action/action-type";

let inicialState = {
    allFood:[],
    allFoodCopy:[],
    allFoodBackUp:[],
    details:{},
    diets:[],
    numPage: 1,
    currentPage:0
}


function rootReducer(state = inicialState, action){
    switch(action.type){
        case GET_USER: 
            return{
                ...state,
                allFood: [...action.payload].splice(0, 9),
                allFoodCopy: action.payload,
            }
            break; 

        case PAGINATE:
            const next_page = state.currentPage + 1;
            const prev_page = state.currentPage - 1;
            const firstIndex = action.payload === "next" ? next_page * 9 : prev_page * 9;
            if(action.payload === "next" && firstIndex >= state.allFoodCopy.length){ return[...state]}
            else if (action.payload === "prev" && prev_page < 0){ return[...state]}

            return{
                ...state,
                allFood: [...state.allFoodCopy].splice(firstIndex, 9),
                currentPage: action.payload === "next" ? next_page : prev_page
                
            }

        case ORDER:
            if(action.payload === "az"){
                const allFoodOrder = [...state.allFoodCopy].sort((prev, next) => {
                    if(prev.title > next.title)return 1
                    if(prev.title < next.title)return -1
                    return 0
                })
                return{
                    ...state,
                    allFood:[...allFoodOrder].slice(0, 9),
                    allFoodCopy: allFoodOrder,
                    currentPage: 0
                }
            }else if(action.payload === "za"){
                const allFoodOrder = [...state.allFoodCopy].sort((prev, next) => {
                    if(prev.title > next.title)return -1
                    if(prev.title < next.title)return 1
                    return 0
                })
                return{
                    ...state,
                    allFood:[...allFoodOrder].slice(0, 9),
                    allFoodCopy: allFoodOrder,
                    currentPage: 0
                }
            }


        case GET_BY_ID:
            return {
              ...state,
              allFoodCopy: action.payload,
            };
          

        case CLEAN_DETAIL:
            return {
              ...state,
              details: []
              
            };


        case ORDER_BY_SOURCE:
            let fromApi = state.allFood.filter((allFoods) => !isNaN(+allFoods.id));
            let fromBDD = state.allFood.filter((allFoods) => isNaN(+allFoods.id));
            
            if (action.payload === "API") {
                return {
                    ...state,
                    allFoodCopy: fromApi.slice(0, 9),
                };
            } else if (action.payload === "BDD") {
                return {
                    ...state,
                    allFoodCopy: fromBDD.slice(0, 9),
                };
            } else if(action.payload === "All") {
                return {
                    ...state,
                };
            }
            
        case HANDLE_NUMBER:
            return {
              ...state,
              numPage: action.payload,
              currentPage: 0
            };


        case GET_BY_NAME:
            return {
            ...state,
            allFoodCopy: action.payload,
            allFood: action.payload,
            };


        case GET_TYPE_DIETS:
            return {
            ...state,
            diets: action.payload,
      };
        default:
            return state
            break;
    }

}

export default rootReducer;
